import { FC } from 'react';
import c from 'classnames';
import styles from './button.module.scss';

interface Props {
  text?: string;
  icon?: Element;
}

const Button: FC<Props> = ({ icon, text }) => {
  return (
    <div role="button" className={styles.button}>
      {icon && (
        <div className={styles.containerIcon}>
          <div className={c(styles.icon, text && styles.haveText)}>{icon}</div>
          {text && <div>{text}</div>}
        </div>
      )}
      {!icon && text && <div>{text}</div>}
    </div>
  );
};

Button.defaultProps = {
  icon: null,
  text: '',
};

export default Button;
