import { FC, ReactChild } from 'react';
import c from 'classnames';
import styles from './button.module.scss';

interface Props {
  text?: string;
  icon?: ReactChild;
  circle?: boolean;
  transparent?: boolean;
}

const Button: FC<Props> = ({ icon, text, circle, transparent }) => {
  return (
    <div
      role="button"
      className={c(
        styles.button,
        text && styles.haveText,
        transparent && styles.transparent,
        circle && styles.circle,
      )}
    >
      {icon && (
        <div className={c(text && styles.content)}>
          <div className={c(styles.icon)}>{icon}</div>
          {text && <div className={styles.text}>{text}</div>}
        </div>
      )}
      {!icon && text && <div className={styles.text}>{text}</div>}
    </div>
  );
};

Button.defaultProps = {
  icon: null,
  text: null,
  transparent: null,
  circle: false,
};

export default Button;
