import { FC, ReactChild, useCallback } from 'react';
import c from 'classnames';
import styles from './button.module.scss';

interface Props {
  text?: string;
  circle?: boolean;
  icon?: ReactChild;
  onClick?: Function;
  transparent?: boolean;
}

const Button: FC<Props> = ({ icon, text, circle, transparent, onClick }) => {
  const click = useCallback(() => {
    onClick();
  }, []);

  return (
    <>
      {circle ? (
        <div
          onClick={click}
          className={c(
            styles.button,
            circle && styles.circle,
            transparent && styles.transparent,
          )}
        >
          {icon}
        </div>
      ) : (
        <div
          onClick={click}
          className={c(styles.button, transparent && styles.transparent)}
        >
          <div className={styles.content}>
            {icon && <div className={c(styles.icon)}>{icon}</div>}
            {icon && <div className={styles.text}>{text}</div>}
          </div>
        </div>
      )}
    </>
  );
};

Button.defaultProps = {
  icon: null,
  text: null,
  circle: false,
  transparent: null,
  onClick: () => {},
};

export default Button;
