import { FC, ReactChild } from 'react';
import c from 'classnames';
import styles from './button.module.scss';

interface Props {
  text?: string;
  circle?: boolean;
  icon?: ReactChild;
  transparent?: boolean;
}

const Button: FC<Props> = ({ icon, text, circle, transparent }) => {
  return (
    <>
      {circle ? (
        <div className={c(styles.button, circle && styles.circle)}>{icon}</div>
      ) : (
        <div className={c(styles.button)}>
          {icon && (
            <div>
              {' '}
              classNames={styles.icon} {icon}
            </div>
          )}
          {text}
        </div>
      )}
    </>
