import { FC } from 'react';
import c from 'classnames';
import styles from './button.module.scss';

interface Props {
  text?: string;
  icon?: Element;
  transparent?: boolean;
}

const Button: FC<Props> = ({ icon, text, transparent }) => {
  return (
    <div
      role="button"
      className={c(
        styles.button,
        text && styles.haveText,
        transparent && styles.transparent,
      )}
    >
      {icon && (
        <div className={c(text && styles.content)}>
          <div className={c(styles.icon)}>{icon}</div>
          {text && <div>{text}</div>}
        </div>
      )}
      {!icon && text && <div>{text}</div>}
    </div>
  );
};

Button.defaultProps = {
  icon: null,
  text: null,
  transparent: null,
};

export default Button;
