  text?: string;
  circle?: boolean;
  icon?: ReactChild;
  transparent?: boolean;
}

const Button: FC<Props> = ({ icon, text, circle, transparent }) => {
  return (
    <>
      {circle ? (
        <div
          className={c(
            styles.button,
            circle && styles.circle,
            transparent && styles.transparent,
          )}
